mapper.update:
UPDATE withdrawal_accounts
merchant_agent_id = #{merchantAgentId}, wallet_addr = #{walletAddr}, wallet_name = #{walletName}, status = #{status}, update_time = #{updateTime}, update_by = #{updateBy}, remark = #{remark},
WHERE id = #{id}

UPDATE withdrawal_accounts
SET status = #{status},
    update_time = #{updateTime},
    update_by = #{updateBy}
WHERE id = #{id}

sql.where:
ui.role_id = 4
AND wa.wallet_addr LIKE CONCAT('%', #{walletAddr}, '%')
AND ai.agent_name LIKE CONCAT('%', #{name}, '%')
AND wa.create_time >= #{startTime}
AND wa.create_time <= #{endTime}

ui.role_id = 2
AND wa.wallet_addr LIKE CONCAT('%', #{walletAddr}, '%')
AND mi.merchant_name LIKE CONCAT('%', #{name}, '%')
AND wa.create_time >= #{startTime}
AND wa.create_time <= #{endTime}
